'use strict';

const should = require('chai').Should();
const request = require("supertest");
const config = require('../config');
const invalidToken = "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6IllNRUxIVDBndmIwbXhvU0RvWWZvbWpxZmpZVSIsImtpZCI6IllNRUxIVDBndmIwbXhvU0RvWWZvbWpxZmpZVSJ9..iSwiivwzTY5Q_hXaNguGlKYkUnLtM1y0JD-nuynj-aOX05D7GwnRCIlRT2k5Z-2vb8TbIMA_h7VbS6GG4bA1fCGgidgoLSm8NNaKkUjsHL77FfOiJ7s-Z2DmInGDlQrtYWY5huGd4HbWX3QtcwC-oOIawF-8RSlg55BgJTk-aOy1RuEAoV8f6YWsj4V46xnsRxbekNhb9Sh8JQMb_kxtOZ_nZ222wskJpoIe2ltyNDy423lYb7hTFSYS4FdDSsqgumwc5iNtZe1eTV7Jz6wsZjL8cvQMKwo_2CXNd1xeRDLdnZafK1jbNaBY7l-iGyD-AyUM9mOfBMwlbPjj3pCEWg"

describe('Service Validation Checks', () => {
  let result;
  let oAuth = request(config.api_host.auth + '/oauth2');

  describe('OAuth Client Credentials', () => {
    it('creates a token using principal id and secret', (done) => {

      // 1) Get OAuth Token
      oAuth.post('/token')
        .set('Content-Type', 'application/x-www-form-urlencoded')
        .send(config.auth_params)
        .expect(200)
        .then((res) => {
          res.body.should.be.an('object');
          result = res.body;
          done();
        });
    });
    it('should have an access_token', () => result.access_token.should.exist);
    it('should be a bearer token', () => result.token_type.should.be.equal('Bearer'));
    it('should expire in 1 hour', () => result.expires_in.should.equal('3599'));
    it('should maintain requested scope', () => result.resource.should.be.equal(config.auth_params.resource));
  });

  describe('Service Authentication', () => {

    it('Legal Service returns 403 with expired token', (done) => {
      request(config.api_host.legal).get('/legaltags/dummy')
        .set('Accept', 'application/json')
        .set('Authorization', invalidToken)
        .expect(403, done);
    });

    it('Storage Service returns 403 with expired token', (done) => {
      request(config.api_host.storage).get('/schemas/dummy')
        .set('Accept', 'application/json')
        .set('Authorization', invalidToken)
        .expect(403, done);
    });

    it('Search Service returns 500 with expired token', (done) => {
      request(config.api_host.search).get('/api/search/v2/query')
        .set('Accept', 'application/json')
        .set('Authorization', invalidToken)
        .expect(500, done);
    });
  });
});
